pragma solidity ^0.5.0;
import "./SuperAdmin.sol";

contract Course{
   address payable public admin;
   uint public accountPrice;
   SuperAdmin superAdmin;

   struct CourseDetails {
        uint id;
        string name;
        uint CourseCount;
    }
     // Store Candidates Count
    uint public CourseCount;


    mapping(address => CourseDetails) public Courses;
    address[] public CoursesAccounts;

    constructor(SuperAdmin _superAdmin, uint256 _Price) public{
        superAdmin = _superAdmin;
        accountPrice = _Price;
        admin =  msg.sender;
    }

    event CourseCreatedEvent (
        uint  id
    );

    function getCoursesAccounts() view public returns(address[] memory) {
        require(msg.sender == admin);
        return CoursesAccounts;
    }

    function getCourses(address _address) view public returns (uint, string memory , uint) {
        require(msg.sender == admin);
        return (Courses[_address].id, Courses[_address].name, Courses[_address].CourseCount);
    }

    function addCourse (string memory _name) public payable {
        require(msg.value == accountPrice);
        require(bytes(_name).length > 0);
        require(address(msg.sender).balance >= accountPrice);
        require(msg.sender != admin);
        CourseCount ++;
        Courses[msg.sender] = CourseDetails(CourseCount, _name, 0);
        CoursesAccounts.push(msg.sender);
        address(admin).transfer(msg.value);
        emit CourseCreatedEvent(CourseCount);
    }
}
